class Geissweb_Euvatgrouper_Model_System_Config_Source_Validationperiod {

    const EVERY_LOGIN = 0;
    const EVERY_MONTH = 1;
    const EVERY_THREE_MONTHS = 3;
    const EVERY_SIX_MONTHS = 6;
    const EVERY_YEAR = 12;


    /**
     * Options getter
     * @return array
     */
    public function toOptionArray()
    {
        return array(
            array('value' => self::EVERY_LOGIN, 'label' => Mage::helper('euvatgrouper')->__('On every login')),
            array('value' => self::EVERY_MONTH, 'label' => Mage::helper('euvatgrouper')->__('Every month')),
            array('value' => self::EVERY_THREE_MONTHS, 'label' => Mage::helper('euvatgrouper')->__('Every 3 months')),
            array('value' => self::EVERY_SIX_MONTHS, 'label' => Mage::helper('euvatgrouper')->__('Every 6 months')),
            array('value' => self::EVERY_YEAR, 'label' => Mage::helper('euvatgrouper')->__('Every year')),
        );
    }
}